import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Login from "./Login";
import Home from "./Home"; // Your existing components
import Search from "./Search";
import Profile from "./Profile";
import MelonImage from "./melon.webp"; // Replace with the correct path to your image

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        // Check login state from localStorage
        const loggedIn = localStorage.getItem("isLoggedIn");
        setIsLoggedIn(loggedIn === "true");
    }, []);

    const handleLogin = () => {
        setIsLoggedIn(true);
    };

    if (!isLoggedIn) {
        return <Login onLogin={handleLogin} />;
    }

    return (
        <BrowserRouter>
            {/* White Top Bar */}
            <div
                style={{
                    backgroundColor: "white",
                    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
                    position: "fixed",
                    top: "0",
                    width: "100%",
                    zIndex: "1000",
                }}
            >
                <nav
                    style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                        padding: "10px 20px",
                        maxWidth: "1200px",
                        margin: "0 auto",
                    }}
                >
                    {/* Left Section: Symbol + Buttons */}
                    <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                        <img
                            src={MelonImage}
                            alt="App Symbol"
                            style={{
                                width: "50px",
                                height: "50px",
                                borderRadius: "50%",
                                objectFit: "cover",
                            }}
                        />
                        <Link to="/">
                            <button
                                style={{
                                    padding: "10px 20px",
                                    fontSize: "16px",
                                    backgroundColor: "red",
                                    color: "white",
                                    border: "none",
                                    borderRadius: "40px",
                                    cursor: "pointer",
                                }}
                            >
                                Home
                            </button>
                        </Link>
                        <Link to="/search">
                            <button
                                style={{
                                    padding: "10px 20px",
                                    fontSize: "16px",
                                    backgroundColor: "#007BFF",
                                    color: "white",
                                    border: "none",
                                    borderRadius: "40px",
                                    cursor: "pointer",
                                }}
                            >
                                Search
                            </button>
                        </Link>
                    </div>

                    {/* Right Section: Profile Button */}
                    <div>
                        <Link to="/profile">
                            <button
                                style={{
                                    padding: "10px 20px",
                                    fontSize: "16px",
                                    backgroundColor: "#28A745",
                                    color: "white",
                                    border: "none",
                                    borderRadius: "40px",
                                    cursor: "pointer",
                                }}
                            >
                                Profile
                            </button>
                        </Link>
                    </div>
                </nav>
            </div>

            {/* App Routes */}
            <div style={{ marginTop: "80px" }}>
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/search" element={<Search />} />
                    <Route path="/profile" element={<Profile />} />
                </Routes>
            </div>
        </BrowserRouter>
    );
};

export default App;
