import React from 'react';
import Slider from 'react-slick';

function Search() {
    const genres = [
        { name: "Fiction", books: ["The Great Gatsby", "1984", "To Kill a Mockingbird"] },
        { name: "Mystery", books: ["Sherlock Holmes", "Gone Girl", "The Da Vinci Code"] },
        { name: "Fantasy", books: ["Harry Potter", "The Hobbit", "Game of Thrones"] },
        { name: "Sci-Fi", books: ["Dune", "Foundation", "Neuromancer"] },
        { name: "Biography", books: ["Steve Jobs", "Becoming", "Long Walk to Freedom"] },
    ];

    // Custom Arrow Components
    const CustomPrevArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                left: '0',
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                zIndex: 1,
            }}
        >
            ◀
        </button>
    );

    const CustomNextArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                right: '0',
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                zIndex: 1,
            }}
        >
            ▶
        </button>
    );

    const sliderSettings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        arrows: true,
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />,
        swipe: true, // Enable touch gestures
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Search for Books</h1>
            <input
                type="text"
                placeholder="Search for a book or genre"
                style={{
                    padding: '10px',
                    width: '80%',
                    marginBottom: '20px',
                    borderRadius: '5px',
                    border: '1px solid #ddd',
                }}
            />
            <button
                style={{
                    padding: '10px 20px',
                    fontSize: '16px',
                    backgroundColor: '#007BFF',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
            >
                Search
            </button>

            <h2>Explore Genres</h2>
            <div style={{ marginTop: '20px' }}>
                {genres.map((genre, index) => (
                    <div key={index} style={{ marginBottom: '40px' }}>
                        <h3>{genre.name}</h3>
                        <div style={{ position: 'relative', padding: '0 20px' }}>
                            <Slider {...sliderSettings}>
                                {genre.books.map((book, bookIndex) => (
                                    <div
                                        key={bookIndex}
                                        style={{
                                            padding: '10px',
                                            textAlign: 'center',
                                            border: '1px solid #ddd',
                                            borderRadius: '5px',
                                        }}
                                    >
                                        <h4>{book}</h4>
                                    </div>
                                ))}
                            </Slider>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Search;
