import React, { useState } from 'react';
import Slider from 'react-slick';
import './Modal.css'; // Add a separate CSS file for modal styles

function Home() {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedBook, setSelectedBook] = useState(null);

    const recommendedBooks = [
        { title: "The Great Gatsby", author: "F. Scott Fitzgerald", reviews: ["Review", "Review"] },
        { title: "To Kill a Mockingbird", author: "Harper Lee", reviews: ["Review", "Review"] },
        { title: "1984", author: "George Orwell", reviews: ["Review", "Review"] },
        { title: "Pride and Prejudice", author: "Jane Austen", reviews: ["Review", "Review"] },
        { title: "Moby Dick", author: "Herman Melville", reviews: ["Review", "Review"] },
    ];

    const openModal = (book) => {
        setSelectedBook(book);
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setSelectedBook(null);
        setIsModalOpen(false);
    };

    const CustomPrevArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                left: '-40px',
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                zIndex: 1,
            }}
        >
            ◀
        </button>
    );

    const CustomNextArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                right: '-40px',
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                zIndex: 1,
            }}
        >
            ▶
        </button>
    );

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        arrows: true,
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />,
        swipe: true,
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px', position: 'relative' }}>
            {isModalOpen && selectedBook && (
                <div className="modal-container" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <h2>{selectedBook.title}</h2>
                        <p>By {selectedBook.author}</p>
                        <h3>Reviews</h3>
                        <ul>
                            {selectedBook.reviews.map((review, index) => (
                                <li key={index}>{review}</li>
                            ))}
                        </ul>
                        <button onClick={closeModal} className="close-button">Close</button>
                    </div>
                </div>
            )}
            <h1>Welcome to the Book Recommendation App</h1>
            <h2>Recommended for You</h2>
            <div style={{ position: 'relative', padding: '0 20px' }}>
                <Slider {...settings}>
                    {recommendedBooks.map((book, index) => (
                        <div
                            key={index}
                            style={{ padding: '10px', textAlign: 'center', cursor: 'pointer' }}
                            onClick={() => openModal(book)}
                        >
                            <h3>{book.title}</h3>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
}

export default Home;
