import React from 'react';
import Slider from 'react-slick';

function Search() {
    const genres = [
        { name: "Fiction", description: "Stories that captivate your imagination" },
        { name: "Mystery", description: "Unravel thrilling mysteries" },
        { name: "Fantasy", description: "Dive into magical worlds" },
        { name: "Sci-Fi", description: "Explore futuristic tales" },
        { name: "Biography", description: "Learn about inspiring lives" },
    ];

    // Custom Arrows for Slider
    const CustomPrevArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                left: '0',
                zIndex: '1',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
            }}
        >
            ◀
        </button>
    );

    const CustomNextArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                right: '0',
                zIndex: '1',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
            }}
        >
            ▶
        </button>
    );

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        arrows: true,
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />,
        swipe: true, // Enable touch gestures
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Search for Books</h1>
            <input
                type="text"
                placeholder="Search for a book or genre"
                style={{ padding: '10px', width: '80%', marginBottom: '20px' }}
            />
            <button
                style={{
                    padding: '10px 20px',
                    fontSize: '16px',
                    backgroundColor: '#007BFF',
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                }}
            >
                Search
            </button>
            <h2>Explore Genres</h2>
            <div style={{ position: 'relative', padding: '0 20px' }}>
                <Slider {...settings}>
                    {genres.map((genre, index) => (
                        <div key={index} style={{ padding: '10px', textAlign: 'center' }}>
                            <h3>{genre.name}</h3>
                            <p>{genre.description}</p>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
}

export default Search;
