import React from 'react';
import Slider from 'react-slick';

function Home() {
    const recommendedBooks = [
        { title: "The Great Gatsby" },
        { title: "To Kill a Mockingbird" },
        { title: "1984" },
        { title: "Pride and Prejudice" },
        { title: "Moby Dick" },
    ];

    // Custom Arrows for Slider
    const CustomPrevArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                left: '-40px', // Adjusted to move outside the slider
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                zIndex: 1,
            }}
        >
            ◀
        </button>
    );

    const CustomNextArrow = ({ onClick }) => (
        <button
            onClick={onClick}
            style={{
                position: 'absolute',
                right: '-40px', // Adjusted to move outside the slider
                top: '50%',
                transform: 'translateY(-50%)',
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                zIndex: 1,
            }}
        >
            ▶
        </button>
    );

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        arrows: true,
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />,
        swipe: true, // Enable touch gestures
        responsive: [
            {
                breakpoint: 768, // For smaller screens
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Welcome to the Book Recommendation App</h1>
            <h2>Recommended for You</h2>
            <div style={{ position: 'relative', padding: '0 20px' }}>
                <Slider {...settings}>
                    {recommendedBooks.map((book, index) => (
                        <div key={index} style={{ padding: '10px', textAlign: 'center' }}>
                            <h3>{book.title}</h3>
                        </div>
                    ))}
                </Slider>
            </div>
        </div>
    );
}

export default Home;
